def loadMovieNames():
    movieNames = {}
    with open('/tmp/u.item', 'r', encoding = 'iso-8859-1') as f:
        for line in f:
            fields = line.strip().split('|')
            movieNames[int(fields[0])] = fields[1]
    return movieNames
